import React from "react";
import { Link } from "react-router-dom";

import styles from "./user.module.scss";

const Users = (props) => {
  if (props.loading) {
    return (
      <div className={styles.wrapper + " skelet-loading"}>
        <div className={styles.card}>
          <img
            style={{ minWidth: "50px" }}
            src="data:image/gif;base64,"
            alt="loading"
            className="loading"
          />
          <div>
            <span className="loading-after"></span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={styles.wrapper}>
      <Link
        to={"/users/" + props.user.user_id}
        className={styles.card}
        title={props.user.user_login}
      >
        <img src={props.user.channel_image} alt="avatar" />
        <div>
          <span>{props.user.user_login}</span>
        </div>
      </Link>
    </div>
  );
};

export default Users;
